function displayUser(row, i, table)
{http://localhost:8080/xwiki/bin/edit/XWSCode/AddMembersToWorkspace?editor=object
  var tr = document.createElement('tr');
  if (i % 2 == 0) {
    tr.className = "even";
  } else {
    tr.className = "odd";
  }
  if(row.groupname) {  // group request
  	text = row.groupname; 
	id = row.groupid;
        divClass = "group";
  }
  else {  // user request
  	text = row.firstname + " " + row.lastname;
	id = row.username;
        divClass = "user";
  }
  var td = document.createElement('td');
  var textDiv = document.createElement('div');
  var arrowDiv = document.createElement('div');
  textDiv.setAttribute("style", "width: 80%; float: left;");
  textDiv.style.width = "80%";
  textDiv.style.styleFloat = "left";
  textDiv.href="javascript:void(0)";
  td.id = "left_" + id;
  textDiv.appendChild(document.createTextNode(text) );

  if(!$('right_'+id)){
    var a = document.createElement('a');
    a.id = "a_" + id;
    a.setAttribute("style","cursor:pointer");
    a.href = "javascript:void(0);" + "pushInList(\""+id+"\",\""+text.replace(/\"/,"\\\"")+"\",\""+divClass+"\")";
    a.appendChild(getAddImageNode());
    arrowDiv.appendChild(a);
  }
  
  td.appendChild(textDiv);
  td.appendChild(arrowDiv);
  tr.appendChild(td);

  return tr;
}
function makeRemoveFromListHandler(userId,userName,type){
   return function(ev){
     return removeUserFromList(userId,userName,type);
   }
}
/**
 * Pushes this user or group in the list of selected users and groups
 * @param {String} id the document name of the group or user pushed
 * @param {String} name the pretty name of the pushed group or user
 * @param {String} type the type of the pushed element : "user" or "group"
 */
function pushInList(userId, userName, type) {
  var rightUsernameDiv = document.createElement('div');
  var rightArrowDiv = document.createElement('div');
  var tableLine = document.createElement('tr');
  var tableCell = document.createElement('td');
  var userEntry = document.createElement('span');
  var a = document.createElement('a');

  rightUsernameDiv.className = type;
  userEntry.id = "right_" + userId;
  userEntry.appendChild(document.createTextNode(userName));

  a.id = "right_a_" + userId;
  a.setAttribute("style","cursor:pointer");

  Event.observe(a,'click',makeRemoveFromListHandler(userId,userName,type));
  a.style.bgColor = "red";
  a.appendChild(getDelImageNode());

  rightArrowDiv.appendChild(a);
  rightArrowDiv.setAttribute("style", "float: left;");

  rightUsernameDiv.setAttribute("style", "width: 80%; float: left;");
  rightUsernameDiv.style.width = "80%";
  rightUsernameDiv.style.styleFloat = "left";
  rightUsernameDiv.appendChild(userEntry);
  //rightUsernameDiv.appendChild(rightArrowDiv);

  tableCell.appendChild(rightUsernameDiv);
  tableCell.appendChild(rightArrowDiv);

  tableLine.appendChild(tableCell);

  $('selectedUsersTableBody').appendChild(tableLine);

  var tdnode = $('a_'+userId);
  tdnode.parentNode.removeChild(tdnode);
}
function getAddImageNode(){
  var img = document.createElement('img');
  img.src = "$xwiki.getSkinFile('images/addtolist.png')";
  return img;
}
function getDelImageNode(){
  var img = document.createElement('img');
  img.src = "$xwiki.getSkinFile('images/delete.png')";
  img.setAttribute("style","padding-right:5px");
  return img;
}

function makePutUserInListHandler(userId,userName,type){
   return function(ev){
     return pushInList(userId,userName,type);
   }
}
/**
 * removes the passed user or group from the selected list
 * @param {String} id the document name of the user or group to remove from selected
 * @param {String} name the pretty name of user of group to remove
 */
function removeUserFromList(id, name, type){
  if($('left_'+id)){
    var a = document.createElement('a');
    a.id = "a_" + id;
    a.setAttribute("style","cursor:pointer");
    Event.observe(a,'click',makePutUserInListHandler(id,name,type));
    a.appendChild(getAddImageNode());
    var aDiv = document.createElement('div');
    aDiv.appendChild(a)
    $('left_'+id).parentNode.lastChild.appendChild(aDiv);
  }
  var d = $('right_'+id);
  var tr = d.parentNode.parentNode.parentNode;
  tr.parentNode.removeChild(tr);
}

